INSERT INTO `lib_video` (`id_video`, `title`, `summary`, `url_video`, `url_image`, `create_time`) VALUES
(1, 'Tên lửa gieo mưa suýt rơi vào người đi bộ ở Trung Quốc', 'Video ghi lại khoảnh khắc quả tên lửa gieo mưa nhân tạo rơi xuống vỉa hè đúng nơi một nhóm người đi bộ vừa đi qua tại tỉnh Tứ Xuyên, Tây Nam Trung Quốc vào hôm 29/8.', 'http://znews-mcloud-bf-s2-te-vnso-zn-9.zingcdn.me/zrhvcOBqrLw/4e4a5f704025af7bf634/b4701519c46b2b35727a/720/66a25a1ba87941271868.mp4?authen=exp=1662103437~acl=/zrhvcOBqrLw/*~hmac=b022f6db6c0fe86d1451e0174924e8de', 'https://znews-photo.zingcdn.me/w360/Uploaded/ifyiv/2022_08_31/Cloud_seeding_rocket_nearly_hits_pedestrians_in_China_as_it_falls_on_pavement.00_00_06_20.Still002.jpg', '2022-08-31 15:11:01'),
(2, 'Bị vali \'đuổi theo\', người phụ nữ ngã nhào trên thang cuốn', 'Đoạn video từ CCTV cho thấy chiếc vali bất ngờ lăn xuống thang cuốn, đâm sầm vào một người phụ nữ khác đang đi phía trước. Vụ việc xảy ra ở Chiết Giang, Trung Quốc vào ngày 29/8.', 'http://znews-mcloud-bf-s2-te-vnso-zn-30.zingcdn.me/HWvLjDw4t2k/0d924f49af14404a1905/5a5d591b976e7830217f/480/981355644709ae57f718.mp4?authen=exp=1662807733~acl=/HWvLjDw4t2k/*~hmac=c09d693b3b020abc7fadc86d7246cbf0', 'https://znews-photo.zingcdn.me/Uploaded/qnuots/2022_09_07/ezgif.com_gif_maker_26_.gif.jpg', '2022-09-08 21:29:12'),
(3, 'Xếp hàng vài tiếng giữa trưa, chờ ăn cháo \'nhà nghèo', 'Quán cháo đậu cà trên phố Đào Duy Từ và Hàng Vôi của gia đình bà Võ Thị Kim Oanh luôn trong tình trạng quá tải, khách chen chúc tìm chỗ, xếp hàng dài chờ đợi.', 'http://znews-mcloud-bf-s2-te-vnso-zn-1.zingcdn.me/QQ3srx9Z6I4/b3b9d75daa01455f1c10/10f55eef1c9bf3c5aa8a/480/cf4469506a3d8363da2c.mp4?authen=exp=1662813123~acl=/QQ3srx9Z6I4/*~hmac=3fe0e3cb72d067d358aa03df2489c196', 'https://znews-photo.zingcdn.me/w660/Uploaded/wpdhnccwv/2022_09_08/base64_1658892741435343026726.jpeg', '2022-09-08 21:29:12'),
(4, 'Ba chàng trai cứu một gia đình khỏi vụ cháy nhà', 'Đoạn video quay lại cảnh 3 thiếu niên ở Michigan (Mỹ) vô tình đi qua một căn nhà đang bốc cháy và nhanh chóng giải cứu họ. Rất may, chỉ một người bị ngạt khói nhẹ sau vụ việc.', 'http://znews-mcloud-bf-s2-te-vnso-pt-2.zingcdn.me/DW5lkzXqXA8/f88abcf296a179ff20b0/f22ca6d59eae71f028bf/480/df8143ea6d8784d9dd96.mp4?authen=exp=1662807884~acl=/DW5lkzXqXA8/*~hmac=67f78869bad97d76cc1e44ed393b5ede', 'https://znews-photo.zingcdn.me/w660/Uploaded/pgi_dhnatxunv/2022_09_05/HABFO2UZLNHTDL7WLALU5V5ASM.jpg', '2022-09-08 21:29:12'),
(5, 'Ôtô bốc cháy dữ dội tại Đồng Nai', 'Đoạn video được camera hành trình ghi lại cảnh một ôtô bị cháy dữ dội, tại đoạn gần qua ngã tư Vũng Tàu (Biên Hòa, Đồng Nai).', 'http://znews-mcloud-bf-s2-te-vnso-pt-2.zingcdn.me/ZzG0X5xra7U/905c93bf0ee2e1bcb8f3/76bd25b494c17b9f22d0/480/96ca4aca5aa7b3f9eab6.mp4?authen=exp=1662807941~acl=/ZzG0X5xra7U/*~hmac=090cacabba444f04dd3d6f330687c595', 'https://znews-photo.zingcdn.me/Uploaded/wpdhnccwv/2022_09_07/ezgif.com_gif_maker_9_.gif.jpg', '2022-09-08 21:29:12'),
(6, 'Bé đập phá TV khi bố mẹ vắng nhà', 'Sau khi bố mẹ rời đi, đứa bé dùng gậy đánh golf đồ chơi đập vào màn hình TV. Đoạn video được camera giám sát ghi lại tại tỉnh Phang Nga, miền Nam Thái Lan.', 'http://znews-mcloud-bf-s2-te-vnso-zn-3.zingcdn.me/gyvAdscRg68/c1716b34bd62523c0b73/665661264d57a209fb46/480/5f377ee8b88a51d4089b.mp4?authen=exp=1662807884~acl=/gyvAdscRg68/*~hmac=6a8e7794158649215232f6b65157c3ec', 'https://znews-photo.zingcdn.me/Uploaded/wpdhnccwv/2022_09_03/ezgif.com_gif_maker_6_.gif.jpg', '2022-09-08 21:29:12'),
(7, 'Nữ quái xế múa ba lê khi được yêu cầu thử nồng độ cồn', 'Sở cảnh sát bang Pinellas công bố video về lần kiểm tra mức độ tỉnh táo của nữ tài xế. Sau khi cô đã lái xe tông vào chiếc ôtô khác.', 'http://znews-mcloud-bf-s2-te-vnso-pt-6.zingcdn.me/XJR-sAaoqRg/a8c8ab84cbd2248c7dc3/0a2dcd405931b66fef20/480/70983853f031196f4020.mp4?authen=exp=1662807884~acl=/XJR-sAaoqRg/*~hmac=037b39b2fbef2be41805d56196ac0190', 'https://znews-photo.zingcdn.me/w660/Uploaded/pgi_dhnatxunv/2022_09_03/Anh_40.jpg', '2022-09-08 21:29:12');
